      // Colors
      colors: {
        brand: {
          light: '#f0f9ff',
          DEFAULT: '#0ea5e9',
          dark: '#0369a1',
        },
      },

      // Typography
      fontFamily: {
        sans: ['Inter var', ...defaultTheme.fontFamily.sans],
      },

      // Spacing
      spacing: {
        '18': '4.5rem',
        '128': '32rem',
      },

      // Animations
      animation: {
        'bounce-slow': 'bounce 2s infinite',
        'spin-slow': 'spin 3s linear infinite',
      },
    },
  },

  // Variants
  variants: {
    extend: {

 opacity: ['disabled'],
      scale: ['active'],
    },
  },

  // Plugins
  plugins: [
    require('@tailwindcss/forms'),
    // Custom plugin
    function({ addUtilities }) {
      addUtilities({
        '.text-shadow': {
          'text-shadow': '2px 2px 4px rgba(0,0,0,0.1)',
        },
      });
    },
  ],
};
```

